INSERT INTO products (types_id, name, price, description) VALUES (1, 'Adult Grain Free Сухой корм беззерновой для взрослых собак, с ягненком, 14 кг', 1895, 'OWNAT JUST GRAIN FREE. ADULT LAMB – это сбалансированное питание для взрослых собак всех пород, произведённое в Испании.Беззерновой корм с мясом ягнёнка не содержит злаки и существенно снижает риск возникновения пищевой аллергии. ');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Сухой корм для взрослых собак средних и крупных пород, ягненок с индейкой, 3 кг', 1195, 'Сухой корм для взрослых собак всех пород Ягненок с рисом. Для собак всех пород от 1 года. Идеально подходит питомцам с чувствительным пищеварением или склонным к аллергии.Тщательно отобранные, натуральные гипоаллергенные ингредиенты, помогают предотвратить аллергические реакции и пищевую непереносимость, обеспечивая иммунное и пищеварительное здоровье. Деликатный способ приготовления сохраняет все полезные свойства и питательные вещества натуральных ингредиентов обеспечивают отличное физическое состояние питомца.');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Medium Adult Ultra Сухой корм для собак средних пород, с ягненком и рисом, 3 кг', 5472, 'OWNAT ULTRA. MEDIUM LAMB & RICE – это сбалансированное питание для взрослых собак средних пород, произведённое в Испании. В основе корма лежит максимальное количество натуральных ингредиентов: свежее мясо, цельное зерно, свежие овощи и фрукты, а также водоросли, что позволяет донести до питомца пользу продуктов в том виде, в каком задумала природа.');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Комбинезон теплый Стаффорд Терьер мальчик зеленый', 3318, 'Комбинезон теплый для собак от бренда RUNGO – простой и эффективный способ сделать прогулку с собакой комфортной в любую непогоду. Комбинезон RUNGO защитит Вашего питомца от сырости, загрязнений и переохлаждения.');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Свитер вязаный для кошек и собак, 25 см, розовый', 1202, 'Теплый, мягкий и приятный на ощупь вязаный свитер от бренда RURRI поможет вашему питомцу не только согреться, но и быть самым модным на прогулке!');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Куртка со шлейкой розовая 4XL', 3000, 'Одежда для собак от Petmax - это качественные, модные изделия, которые подойдут любой собаке.');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Ошейник для собак и кошек, 1,5х35 см, голубой с принтом', 346, 'Ошейник для собак и кошек от бренда Rurri – стильный и износостойкий аксессуар для комфортных прогулок');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Шлейка-жилетка для собак Air, обхват груди 30-35 см, лента 10 мм, розовая', 1715, 'Шлейка для собак от бренда Rungo – это надежный и безопасный аксессуар для вашего питомца. Размеры: 30-35 см');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Рулетка New COMFORT S ремень 5 м до 15 кг, розовый', 1978, 'Видимость является девизом серии рулеток NEON. Использование сильных отражающих элементов обеспечивает повышенную безопасность для собак и их владельцев после наступления темноты. ');
INSERT INTO products (types_id, name, price, description) VALUES (1, 'Рюкзак для переноски кошек и собак мелких пород, 34х43х24 см', 3760, 'Рюкзак от бренда Rurri подойдет для перевозки кошек и собак мелких пород. Размеры: 34х43х24 см');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Adult Sterilized Classic Сухой корм для стерилизованных кошек, с курицей, 4 кг', 4269, 'OWNAT CLASSIC STERILIZED – это сбалансированный корм для стерилизованных кошек. Стерилизованные кошки и кастрированные коты имеют естественную склонность к ожирению. Именно поэтому данный продукт с низким содержанием жиров, способствующий потреблению меньшего количества калорий, которые компенсируются за счет высокого содержания белков, обеспечивающих правильное поддержание мышечной массы кошки.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Корм сухой для стерилизованных кошек, с лососем, 1,5 кг', 1346, 'MEALFEEL STERILIZED – сухой рацион на основе свежего мяса, разработанный специально для стерилизованных кошек и кастрированных котов.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Сухой корм для котят, ягненок с индейкой, 2 кг', 2145, 'Идеально подходит для нежной пищеварительной системы котят. Высокое содержание необходимых протеинов, полезных жиров, аминокислот и витаминов гарантирует оптимальную энергетическую ценность для здорового роста и развития котенка.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Дом-когтеточка (62х50х156 см) из сизаля и плюша для кошек', 15653, 'Дом-когтеточка 62х50х156 см - это многоуровневый игровой комплекс, который предоставит вашему питомцу отличное место для отдыха и сна. Идеален для дома с двумя и более питомцами. ');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Лежак с искусственным мехом для кошек', 1089, 'Удобный лежак с искусственным мехом для кошек и собак мелких пород от бренда Rurri станет уютным местом отдыха для вашего питомца. Размеры: 46х46х15 см');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Лежанка-когтеточка Сота-Мини', 589, 'Когтеточка-лежанка от бренда Домоседы развлечет питомца и позволит ему удовлетворить свои естественные инстинкты.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Платье для кошек', 699, 'Платье для собак и кошек от бренда Rurri защитит питомца от палящих солнечных лучей в жаркую погоду, поможет комфортно чувствовать себя в прохладные дни.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Свитер для кошек', 1026, 'Одежда для собак и кошек от бренда Petmax – это качественные и модные изделия. Акриловый свитер согреет четвероногого друга в прохладную погоду.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Футболка для кошек', 626, 'Футболка с принтом для собак и кошек от бренда Rurri защитит питомца от палящих солнечных лучей в жаркую погоду, поможет комфортно чувствовать себя в прохладные дни.');
INSERT INTO products (types_id, name, price, description) VALUES (2, 'Рюкзак с сетчатым окошком для переноски кошек', 2118, 'Рюкзак от бренда Rurri – это удобный и надежный аксессуар для перевозки кошек и собак небольшого размера. Размеры: 38х36х25 см.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Grandmix Pappagalli комплексный, основной для крупных попугаев 2 кг', 2620, 'Любимым лакомством крупных попугаев станет корм бренда Падован. Оптимальные пропорции зерновых и злаковых культур в составе смеси располагают к ежедневному применению. Натуральные витаминизированные и минеральные добавки обеспечивают птице комплексный рацион, а сбалансированное питание благоприятно влияет на здоровье домашнего питомца!');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Корм для средних попугаев, основной рацион, 1кг', 489, 'Сбалансированный корм для ежедневного кормления средних попугаев, идеально подходит для неразлучников и корелл. Разнообразный состав включает самые полезные и любимые средними попугаями зерна и семена, а также рябину и морковь для поддержки зрения и здоровья кожи.Корм обогащен кальцием для крепких костей и клюва, а также семенами льна, содержащими жирные кислоты для здоровья кожи и оперения.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Корм для волнистых попугаев, 800 гр.', 523, 'Основной рацион GRUMS содержит различные компоненты для разнообразного питания волнистых попугаев. Зерновая смесь насыщена витаминами и микроэлементами, необходимыми для здорового и активного образа жизни пернатых.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Игрушка для птиц связка из травяного и морковного печенья, 139 г', 323, 'Игрушка для птиц связка из травяного и морковного печенья, не даст скучать вашему питомцу. Игрушка выполнена из натуральных материалов.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Тарелочка с глиняным печеньем для птиц', 822, 'Игрушка-угощение для птиц и грызунов от бренда Petmax порадует вашего маленького друга. Размер: диаметр тарелки: 8 см, высота 2 см. Длина веревки с крючком 26 см.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Забавные качели для средних попугаев', 320, 'идеально разнообразит ежедневный рацион, благоприятно влияет на уход за клювом, создает психологически полезную имитацию самостоятельного добывания корма, привлекает попугайчика как необычное развлечение, стержень Орешника служит натуральной дополнительной жердочкой для отдыха попугая и удобного потребления зерен с игрушки, приятный витаминный бонус - полезные сушеные овощи и фрукты.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Миска для птиц металл с крепежом 300мл', 278, 'Металлическая миска для птиц Petmax с крепежом 300 мл предназначена для крупных домашних птиц. Ее можно удобно закрепить за прутья клетки и достать в любой момент для очистки и наполнения. Посуда для птичьей клетки достаточно глубокая, что позволяет наполнить ее кормом на несколько дней вперед, чтобы ваш питомец всегда мог подкрепиться.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Поилка для птиц 15 см', 275, 'Удобная поилка для птиц Трикси обеспечит пернатого питомца чистой водой даже во время вашего длительного отсутствия. Она представляет собой пластиковую колбу, оснащенную дозатором, который не позволяет воде вылиться всей сразу, а подает ее по мере необходимости. За счет прозрачной текстуры поилки, можно легко контролировать уровень воды в ней. Высота изделия – 15 см.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Купалка Треви 4406 для птиц, 14 x15,7x13,8 см', 242, 'Доступны различные расцветки; цвет поставляемого товара будет зависеть от наличия на складе. Без возможности выбора цвета. Trevi - ванночка для канареек и экзотических птиц, которая позволит вашему питомцу помыть свои крылышки в полном уединении. Trevi имеет широкую основу с бортиками для наполнения водой, а также прозрачный верх для защиты от разбрызгивания. Также имеется удобная жердочка.');
INSERT INTO products (types_id, name, price, description) VALUES (3, 'Домик из натуральных материалов для птиц Чудо-кокос', 1359, 'Игрушка для птиц, выполненная из кокоса, наполненного кокосовым волокном. Верхняя часть состоит из плетеного шара, завернутого в кукурузные листья.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Корм для водных черепах ReptoMin гранулы, 1л/300г', 1160, 'Основной корм для водяных черепах TetraFauna Reptomin - основа полезного и сбалансированного рациона питомцев. В его составе есть большое количество полезных питательных веществ, включая обилие протеина и минералов вроде кальция. Все микроэлементы оказывают благотворное влияние на организм, укрепляют мышцы, панцирь, повышают устойчивость к болезням.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Корм для рептилий Reptil Professional Carnivor 3,8 л (1 кг) ведро', 5110, 'Дополнительный корм для плотоядных рептилий, таких, например, как водяные черепахи. Корм состоит из плавающих двухцветных гранул, произведенных с использованием процесса высоко функциональной коэкструзии. Корм, составляющий оболочку-кольцо, содержит сбалансированную смесь, изготовленную из водных организмов, таких как рыба, гаммарус и криль, и привносит в корм высококачественные белки и Омега жирные кислоты.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'ReptoMin Baby Корм для молодых водных черепах, бн. 100мл', 310, 'Корм для молодых водных черепах от Tetra ReptoMin Baby представлен небольшими палочками по 100 мл, которые могут служить основой для сбалансированного повседневного рациона. В составе есть высокое содержание кальция, который способствует нормальному развитию и формированию крепкого панциря. Также смесь содержит достаточное количество полезных микроэлементов и питательных веществ.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Террариум малый открытый розовый 32х20,5х7 см', 216, 'Открытый пластиковый террариум для черепах. Легко чистится. Отличное соотношение цены и качества. Декор не входит в комплект. Материал: пластик. Размер: малый.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Террариум (50 л, 55x33x29,1 см)', 5730, 'Террариум изготавливается из стекла 4 мм. Склеивается качественным клеем-герметиком, предназначенным специально для аквариумов, который безвреден для его обитателей. Очень компактный и удобный. Подходит для рептилий. Террариум с крышкой, патрон E14, вклеенный декоративный мостик с газоном. Сверху крышки есть вентиляция.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Террариум прямоугольный для черепах и рептилий, 25 л', 4528, 'Террариум от бренда WATERA станет надежным домом для ваших экзотических питомцев. Размер: 36х26,5х26 см Объем: 25 литров Преимущества: изготовлен из полированного стекла; стекла склеены специальным высококачественным герметиком; легко ухаживать и мыть.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Декорация природная Коряга затонувшая, XS, 10-20 см', 431, 'Внимание! Так как натуральные коряги имеют природное происхождение и каждая такая декорация уникальна, то окончательный вид товара может отличаться от представленного на фотографии на сайте. Элемент декора для аквариумов и террариумов из натуральных материалов “Коряга затонувшая XS” фирмы PRIME. Основные параметры: Размер: XS 10-20 cм. Вес(гр): 100-300 Материал изготовления: дерево.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Декорация природная для аквариума Камень серый Лао, S, 10-20 см', 871, 'Камень декоративный для оформления аквариума, террариума. Вес одного камня от 1 кг до 2 кг');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Грунт декоративный акриловый 400г белый/красный', 853, 'Декоративный аквариумный грунт безвреден для обитателей аквариума, химически нейтрален. Применяется в аквариумном и флористическом дизайне.');
INSERT INTO products (types_id, name, price, description) VALUES (4, 'Растение фиолетовое', 200, 'Искусственные растения для аквариумов от AquaFantasy помогут создать оригинальный интерьер в любом аквариуме. Качественный пластик выглядит очень реалистично, не влияет на качество воды и прослужит вам очень долго. Пластиковые растения Аквафэнтези предоставят обитателям аквариума место для сна, игр и укрытие. Такие растения совершенно не требуют от владельцев дополнительного ухода, в отличие от живых растений.');
INSERT INTO products (types_id, name, price, description) VALUES (5, 'Корм для крыс, 900 гр.', 450, 'Разнообразное питание, которое достигается за счет оптимально подобранного состава хлопьев. Содержит все необходимые питательные вещества и микроэлементы. Улучшает здоровье, жизненную силу и богатство красок. С формулой BioActive для продолжительной и здоровой жизни ваших питомцев. С формулой Clean & Clear Water: улучшает усваиваемость корма. ');
INSERT INTO products (types_id, name, price, description) VALUES (5, 'Корм для кроликов 900 гр.', 450, 'Полнорационный корм, содержащий комплекс белков, жиров, углеводов, минеральных веществ и витаминов. Разработан с учетом специфических потребностей кроликов и отличается высоким содержанием клетчатки, необходимой для правильного пищеварения. Состав корма обогащен сушеными овощами, плющеными бобами и плодами редких растений.');
INSERT INTO products (types_id, name, price, description) VALUES (5, 'Корм для морских свинок 900 гр.', 450, 'Полнорационный корм с добавлением витаминов и минеральных веществ. Разработан с учетом специфических потребностей морских свинок и содержит повышенное количество витамина С, жизненно необходимого для их здоровья. Разнообразный состав корма включает в себя травяные гранулы, воздушные ингредиенты и хлопья, семена, овощи и редкие плоды.');
INSERT INTO products (types_id, name, price, description) VALUES (5, 'Подстилка для хомяков 13х13 см', 365, 'Подстилка для хомяка Трикси обеспечит максимально комфортную жизнь вашему питомцу. Наличие летней и зимней стороны делают изделие очень практичным и функциональным. Изготовлена подстилка из качественной, плотной ткани и имеет мягкий наполнитель из полиэфира. Порадуйте своего питомца удобным аксессуаром в его клетку.');
INSERT INTO products (types_id, name, price, description) VALUES (5, 'Колесо для грызунов красный d 14,5см h 7,5см', 256, 'Изготовлено из высококачественного пластика. Крепится к стенке клетки. Обеспечит необходимый уровень активности вашему питомцу. Легко моется. ЦВЕТА В АССОРТИМЕНТЕ');
INSERT INTO products (types_id, name, price, description) VALUES (5, 'Клетка для мелких грызунов 47x30x37 см розовая', 2739, 'Клетка для мелких грызунов от бренда Petmax станет настоящим домом для вашего питомца. Размер: 47х30х37 см.');
